20250610030412	{"-- 本番環境：管理者ポリシーと関数を全て削除\n\n-- 管理者関連のRLSポリシーを削除\nDROP POLICY IF EXISTS \\"Admin can view all profiles\\" ON public.profiles;\nDROP POLICY IF EXISTS \\"Admin can update all profiles\\" ON public.profiles;\nDROP POLICY IF EXISTS \\"Admin can delete all profiles\\" ON public.profiles;\nDROP POLICY IF EXISTS \\"Admin can delete active battles\\" ON public.active_battles;\nDROP POLICY IF EXISTS \\"Admin can delete archived battles\\" ON public.archived_battles;\nDROP POLICY IF EXISTS \\"Admin can delete submissions\\" ON public.submissions;\nDROP POLICY IF EXISTS \\"Admin can delete votes\\" ON public.battle_votes;\nDROP POLICY IF EXISTS \\"Admin can delete posts\\" ON public.posts;\nDROP POLICY IF EXISTS \\"Admin can delete comments\\" ON public.comments;\nDROP POLICY IF EXISTS \\"Admin can delete notifications\\" ON public.notifications;\n\n-- 管理者関連関数を削除\nDROP FUNCTION IF EXISTS public.admin_delete_active_battle(uuid) CASCADE;\nDROP FUNCTION IF EXISTS public.admin_delete_archived_battle(uuid) CASCADE;\nDROP FUNCTION IF EXISTS public.admin_delete_submission(uuid) CASCADE;\nDROP FUNCTION IF EXISTS public.admin_delete_user_account(uuid) CASCADE;\nDROP FUNCTION IF EXISTS public.is_current_user_admin() CASCADE;\n\n-- 他のバリエーションも削除\nDROP FUNCTION IF EXISTS public.admin_delete_active_battle(text) CASCADE;\nDROP FUNCTION IF EXISTS public.admin_delete_archived_battle(text) CASCADE;\nDROP FUNCTION IF EXISTS public.admin_delete_submission(text) CASCADE;\nDROP FUNCTION IF EXISTS public.admin_delete_user_account(text) CASCADE;\n\n-- マイグレーション完了ログ\nDO $$\nBEGIN\n    RAISE NOTICE 'All admin policies and functions removed successfully from production environment.';\nEND\n$$;"}	remove_admin_policies_and_functions_production	rikujiogino@gmail.com	\N
20250610043118	{"-- テストデータを安全に削除するマイグレーション（修正版）\n\n-- テストユーザーのIDを特定\nDO $$\nDECLARE\n    test_user_ids UUID[] := ARRAY[\n        '11111111-1111-1111-1111-111111111111',\n        '22222222-2222-2222-2222-222222222222', \n        '33333333-3333-3333-3333-333333333333',\n        '44444444-4444-4444-4444-444444444444'\n    ];\n    target_user_id UUID;\nBEGIN\n    -- テストユーザーごとに関連データを削除\n    FOREACH target_user_id IN ARRAY test_user_ids\n    LOOP\n        -- 通知を削除\n        DELETE FROM public.notifications WHERE user_id = target_user_id;\n        \n        -- コメントを削除\n        DELETE FROM public.comments WHERE user_id = target_user_id;\n        \n        -- 投稿を削除\n        DELETE FROM public.posts WHERE user_id = target_user_id;\n        \n        -- バトル投票を削除\n        DELETE FROM public.battle_votes WHERE user_id = target_user_id;\n        \n        -- アーカイブバトルを削除（プレイヤーとして参加したもの）\n        DELETE FROM public.archived_battles \n        WHERE player1_user_id = target_user_id \n           OR player2_user_id = target_user_id \n           OR winner_id = target_user_id;\n        \n        -- アクティブバトルを削除（プレイヤーとして参加したもの）\n        DELETE FROM public.active_battles \n        WHERE player1_user_id = target_user_id \n           OR player2_user_id = target_user_id;\n        \n        -- 投稿を削除\n        DELETE FROM public.submissions WHERE user_id = target_user_id;\n        \n        -- 最後にプロフィールを削除\n        DELETE FROM public.profiles WHERE id = target_user_id;\n        \n        RAISE NOTICE 'Deleted test user and related data: %', target_user_id;\n    END LOOP;\n    \n    RAISE NOTICE 'Test data cleanup completed successfully.';\nEND\n$$;"}	remove_test_data_cleanup_fixed	rikujiogino@gmail.com	\N
