20250612105407	{"-- 20250112120000_fix_user_deletion.sql\n-- ユーザー削除問題の修正: ソフト削除機能と安全な削除関数を追加\n\n-- 1. ソフト削除フラグをprofilesテーブルに追加\nALTER TABLE profiles ADD COLUMN IF NOT EXISTS is_deleted BOOLEAN DEFAULT FALSE;\nALTER TABLE profiles ADD COLUMN IF NOT EXISTS deleted_at TIMESTAMPTZ;\n\n-- 2. 削除されたユーザーを除外するためのインデックス（パフォーマンス向上）\nCREATE INDEX IF NOT EXISTS idx_profiles_not_deleted \nON profiles (id) WHERE is_deleted = FALSE;\n\n-- 3. 安全なユーザー削除関数\nCREATE OR REPLACE FUNCTION safe_delete_user_account(p_user_id UUID)\nRETURNS JSON LANGUAGE plpgsql SECURITY DEFINER AS $$\nDECLARE\n  v_has_active_battles BOOLEAN := FALSE;\n  v_has_archived_battles BOOLEAN := FALSE;\n  v_username TEXT;\nBEGIN\n  -- 現在のユーザー名を取得\n  SELECT username INTO v_username FROM profiles WHERE id = p_user_id;\n  \n  IF NOT FOUND THEN\n    RETURN json_build_object(\n      'success', false,\n      'error', 'User not found'\n    );\n  END IF;\n\n  -- アクティブバトルの存在確認\n  SELECT EXISTS(\n    SELECT 1 FROM active_battles \n    WHERE player1_user_id = p_user_id OR player2_user_id = p_user_id\n  ) INTO v_has_active_battles;\n  \n  -- アーカイブバトルの存在確認\n  SELECT EXISTS(\n    SELECT 1 FROM archived_battles \n    WHERE player1_user_id = p_user_id OR player2_user_id = p_user_id\n  ) INTO v_has_archived_battles;\n  \n  -- アクティブバトルがある場合、またはアーカイブバトルがある場合はソフト削除\n  IF v_has_active_battles OR v_has_archived_battles THEN\n    \n    -- 進行中のバトルがある場合は強制終了処理\n    IF v_has_active_battles THEN\n      UPDATE active_battles \n      SET status = 'PROCESSING_RESULTS',\n          updated_at = NOW()\n      WHERE (player1_user_id = p_user_id OR player2_user_id = p_user_id)\n        AND status = 'ACTIVE';\n    END IF;\n    \n    -- プロフィールをソフト削除（匿名化）\n    UPDATE profiles \n    SET \n      is_deleted = TRUE,\n      deleted_at = NOW(),\n      username = 'deleted-user-' || SUBSTRING(id::text, 1, 8),\n      email = 'deleted-' || SUBSTRING(id::text, 1, 8) || '@deleted.local',\n      avatar_url = NULL,\n      bio = 'このアカウントは削除されました',\n      updated_at = NOW()\n    WHERE id = p_user_id;\n    \n    RETURN json_build_object(\n      'success', true, \n      'method', 'soft_delete',\n      'reason', CASE \n        WHEN v_has_active_battles THEN 'User has active battles'\n        ELSE 'User has battle history'\n      END,\n      'original_username', v_username\n    );\n    \n  ELSE\n    \n    -- バトル履歴もない場合は物理削除を試行\n    BEGIN\n      -- 関連データを安全な順序で削除\n      DELETE FROM notifications WHERE user_id = p_user_id;\n      DELETE FROM comments WHERE user_id = p_user_id;\n      DELETE FROM posts WHERE user_id = p_user_id;\n      DELETE FROM battle_votes WHERE user_id = p_user_id;\n      DELETE FROM submissions WHERE user_id = p_user_id;\n      \n      -- プロフィール削除\n      DELETE FROM profiles WHERE id = p_user_id;\n      \n      RETURN json_build_object(\n        'success', true, \n        'method', 'physical_delete',\n        'reason', 'No battle history found',\n        'original_username', v_username\n      );\n      \n    EXCEPTION WHEN foreign_key_violation THEN\n      -- 外部キー制約エラーの場合はソフト削除にフォールバック\n      UPDATE profiles \n      SET \n        is_deleted = TRUE,\n        deleted_at = NOW(),\n        username = 'deleted-user-' || SUBSTRING(id::text, 1, 8),\n        email = 'deleted-' || SUBSTRING(id::text, 1, 8) || '@deleted.local',\n        avatar_url = NULL,\n        bio = 'このアカウントは削除されました',\n        updated_at = NOW()\n      WHERE id = p_user_id;\n      \n      RETURN json_build_object(\n        'success', true, \n        'method', 'soft_delete_fallback',\n        'reason', 'Foreign key constraints detected',\n        'original_username', v_username\n      );\n    END;\n    \n  END IF;\n  \nEXCEPTION WHEN OTHERS THEN\n  RETURN json_build_object(\n    'success', false, \n    'error', SQLERRM,\n    'error_detail', SQLSTATE\n  );\nEND;\n$$;\n\n-- 4. RLSポリシーを更新（削除されたユーザーを除外）\nDROP POLICY IF EXISTS \\"Users can view all profiles\\" ON profiles;\nCREATE POLICY \\"Users can view all profiles\\" ON profiles\n  FOR SELECT USING (is_deleted = FALSE OR is_deleted IS NULL);\n\nDROP POLICY IF EXISTS \\"Users can update own profile\\" ON profiles;\nCREATE POLICY \\"Users can update own profile\\" ON profiles\n  FOR UPDATE USING (auth.uid() = id AND (is_deleted = FALSE OR is_deleted IS NULL));\n\n-- 5. 削除されたユーザーの投稿/コメントも非表示にするポリシー更新\nDROP POLICY IF EXISTS \\"Anyone can view posts\\" ON posts;\nCREATE POLICY \\"Anyone can view posts\\" ON posts\n  FOR SELECT USING (\n    EXISTS (\n      SELECT 1 FROM profiles \n      WHERE profiles.id = posts.user_id \n        AND (profiles.is_deleted = FALSE OR profiles.is_deleted IS NULL)\n    )\n  );\n\nDROP POLICY IF EXISTS \\"Anyone can view comments\\" ON comments;\nCREATE POLICY \\"Anyone can view comments\\" ON comments\n  FOR SELECT USING (\n    EXISTS (\n      SELECT 1 FROM profiles \n      WHERE profiles.id = comments.user_id \n        AND (profiles.is_deleted = FALSE OR profiles.is_deleted IS NULL)\n    )\n  );\n\n-- 6. 管理者用の削除されたユーザー確認関数\nCREATE OR REPLACE FUNCTION get_deleted_users()\nRETURNS TABLE(\n  id UUID,\n  original_username TEXT,\n  deleted_at TIMESTAMPTZ,\n  had_battles BOOLEAN\n) LANGUAGE plpgsql SECURITY DEFINER AS $$\nBEGIN\n  RETURN QUERY\n  SELECT \n    p.id,\n    CASE \n      WHEN p.username LIKE 'deleted-user-%' THEN 'Unknown'\n      ELSE p.username\n    END as original_username,\n    p.deleted_at,\n    EXISTS(\n      SELECT 1 FROM archived_battles ab \n      WHERE ab.player1_user_id = p.id OR ab.player2_user_id = p.id\n    ) as had_battles\n  FROM profiles p\n  WHERE p.is_deleted = TRUE\n  ORDER BY p.deleted_at DESC;\nEND;\n$$;\n\n-- コメント追加\nCOMMENT ON FUNCTION safe_delete_user_account(UUID) IS 'バトル履歴を考慮した安全なユーザー削除関数。アクティブバトルや履歴がある場合はソフト削除、ない場合は物理削除を実行。';\nCOMMENT ON FUNCTION get_deleted_users() IS '削除されたユーザーの一覧を管理者が確認するための関数。';\nCOMMENT ON COLUMN profiles.is_deleted IS 'ソフト削除フラグ。TRUEの場合、ユーザーは削除済みとして扱われる。';\nCOMMENT ON COLUMN profiles.deleted_at IS 'ソフト削除された日時。is_deleted=TRUEの場合に設定される。';"}	fix_user_deletion	rikujiogino@gmail.com	\N
20250612110843	{"-- 20250112120100_simple_privacy_fix.sql\n-- 削除されたユーザーのプライバシー保護（管理者機能なし）\n\n-- 1. 削除されたユーザー確認関数を無効化（誰も使えないように）\nDROP FUNCTION IF EXISTS get_deleted_users();\n\n-- 2. セキュリティ上問題のある関数をすべて削除\n-- （削除されたユーザー情報へのアクセスを完全に遮断）\n\n-- 3. profilesテーブルのポリシーを確実に更新\n-- 既存の重複ポリシーをクリーンアップ\nDROP POLICY IF EXISTS \\"Public profiles are viewable by everyone\\" ON profiles;\nDROP POLICY IF EXISTS \\"Users can view all profiles\\" ON profiles;\n\n-- 4. アクティブユーザーのみ表示するポリシーを作成\nCREATE POLICY \\"Only active profiles are viewable\\" ON profiles\n  FOR SELECT USING (is_deleted = FALSE OR is_deleted IS NULL);\n\n-- 5. 削除されたユーザーの投稿/コメントも非表示に（既存ポリシー更新）\n-- posts\nDROP POLICY IF EXISTS \\"Allow authenticated users to read all posts\\" ON posts;\nDROP POLICY IF EXISTS \\"Anyone can view posts\\" ON posts;\n\nCREATE POLICY \\"View posts from active users only\\" ON posts\n  FOR SELECT USING (\n    EXISTS (\n      SELECT 1 FROM profiles \n      WHERE profiles.id = posts.user_id \n        AND (profiles.is_deleted = FALSE OR profiles.is_deleted IS NULL)\n    )\n  );\n\n-- comments  \nDROP POLICY IF EXISTS \\"Allow authenticated users to read all comments\\" ON comments;\nDROP POLICY IF EXISTS \\"Anyone can view comments\\" ON comments;\n\nCREATE POLICY \\"View comments from active users only\\" ON comments\n  FOR SELECT USING (\n    EXISTS (\n      SELECT 1 FROM profiles \n      WHERE profiles.id = comments.user_id \n        AND (profiles.is_deleted = FALSE OR profiles.is_deleted IS NULL)\n    )\n  );\n\n-- 6. battle_votesテーブルも削除されたユーザーの投票を非表示に\nDROP POLICY IF EXISTS \\"Public can view battle votes\\" ON battle_votes;\n\nCREATE POLICY \\"View votes from active users only\\" ON battle_votes\n  FOR SELECT USING (\n    user_id IS NULL OR \n    EXISTS (\n      SELECT 1 FROM profiles \n      WHERE profiles.id = battle_votes.user_id \n        AND (profiles.is_deleted = FALSE OR profiles.is_deleted IS NULL)\n    )\n  );\n\n-- 7. archived_battlesとactive_battlesでも削除されたユーザーのバトルを適切に表示\n-- （バトル自体は履歴として残すが、削除されたユーザーは匿名として表示）\n\n-- archived_battlesの表示用ビューを作成（削除されたユーザーを匿名化）\nCREATE OR REPLACE VIEW public_archived_battles AS\nSELECT \n  ab.id,\n  ab.original_battle_id,\n  ab.winner_id,\n  ab.final_votes_a,\n  ab.final_votes_b,\n  ab.battle_format,\n  ab.archived_at,\n  ab.created_at,\n  \n  -- プレイヤー1の情報（削除済みなら匿名化）\n  CASE \n    WHEN p1.is_deleted = TRUE THEN NULL\n    ELSE ab.player1_user_id\n  END as player1_user_id,\n  \n  CASE \n    WHEN p1.is_deleted = TRUE THEN 'deleted-user'\n    ELSE p1.username\n  END as player1_username,\n  \n  -- プレイヤー2の情報（削除済みなら匿名化）\n  CASE \n    WHEN p2.is_deleted = TRUE THEN NULL\n    ELSE ab.player2_user_id\n  END as player2_user_id,\n  \n  CASE \n    WHEN p2.is_deleted = TRUE THEN 'deleted-user'\n    ELSE p2.username\n  END as player2_username,\n  \n  ab.player1_video_url,\n  ab.player2_video_url,\n  ab.player1_rating_change,\n  ab.player2_rating_change,\n  ab.player1_final_rating,\n  ab.player2_final_rating\n\nFROM archived_battles ab\nLEFT JOIN profiles p1 ON ab.player1_user_id = p1.id\nLEFT JOIN profiles p2 ON ab.player2_user_id = p2.id;\n\n-- 8. active_battlesの表示用ビューも作成\nCREATE OR REPLACE VIEW public_active_battles AS\nSELECT \n  ab.id,\n  ab.battle_format,\n  ab.status,\n  ab.votes_a,\n  ab.votes_b,\n  ab.end_voting_at,\n  ab.created_at,\n  ab.updated_at,\n  \n  -- プレイヤー1の情報（削除済みなら匿名化）\n  CASE \n    WHEN p1.is_deleted = TRUE THEN NULL\n    ELSE ab.player1_user_id\n  END as player1_user_id,\n  \n  CASE \n    WHEN p1.is_deleted = TRUE THEN 'deleted-user'\n    ELSE p1.username\n  END as player1_username,\n  \n  -- プレイヤー2の情報（削除済みなら匿名化）\n  CASE \n    WHEN p2.is_deleted = TRUE THEN NULL\n    ELSE ab.player2_user_id\n  END as player2_user_id,\n  \n  CASE \n    WHEN p2.is_deleted = TRUE THEN 'deleted-user'\n    ELSE p2.username\n  END as player2_username,\n  \n  ab.player1_submission_id,\n  ab.player2_submission_id\n\nFROM active_battles ab\nLEFT JOIN profiles p1 ON ab.player1_user_id = p1.id\nLEFT JOIN profiles p2 ON ab.player2_user_id = p2.id;\n\n-- 9. ビューにRLSポリシーを設定\nALTER VIEW public_archived_battles OWNER TO postgres;\nALTER VIEW public_active_battles OWNER TO postgres;\n\n-- 10. submissionsテーブルも削除されたユーザーの投稿を非表示に\nUPDATE submissions \nSET status = 'WITHDRAWN' \nWHERE user_id IN (\n  SELECT id FROM profiles WHERE is_deleted = TRUE\n) AND status = 'WAITING_OPPONENT';\n\n-- 11. コメント追加\nCOMMENT ON VIEW public_archived_battles IS 'アーカイブバトルの公開ビュー。削除されたユーザーは匿名化して表示。';\nCOMMENT ON VIEW public_active_battles IS 'アクティブバトルの公開ビュー。削除されたユーザーは匿名化して表示。';"}	simple_privacy_fix	rikujiogino@gmail.com	\N
20250612111646	{"-- 20250112120200_fix_email_reuse.sql\n-- メールアドレス再利用問題の修正\n\n-- 1. 削除されたユーザーのauth.usersのメールアドレスも匿名化する関数を修正\nCREATE OR REPLACE FUNCTION safe_delete_user_account(p_user_id UUID)\nRETURNS JSON LANGUAGE plpgsql SECURITY DEFINER AS $$\nDECLARE\n  v_has_active_battles BOOLEAN := FALSE;\n  v_has_archived_battles BOOLEAN := FALSE;\n  v_username TEXT;\n  v_original_email TEXT;\n  v_anonymized_email TEXT;\nBEGIN\n  -- 現在のユーザー名とメールアドレスを取得\n  SELECT username INTO v_username FROM profiles WHERE id = p_user_id;\n  SELECT email INTO v_original_email FROM auth.users WHERE id = p_user_id;\n  \n  IF NOT FOUND THEN\n    RETURN json_build_object(\n      'success', false,\n      'error', 'User not found'\n    );\n  END IF;\n\n  -- 匿名化用のメールアドレスを生成\n  v_anonymized_email := 'deleted-' || SUBSTRING(p_user_id::text, 1, 8) || '@deleted.local';\n\n  -- アクティブバトルの存在確認\n  SELECT EXISTS(\n    SELECT 1 FROM active_battles \n    WHERE player1_user_id = p_user_id OR player2_user_id = p_user_id\n  ) INTO v_has_active_battles;\n  \n  -- アーカイブバトルの存在確認\n  SELECT EXISTS(\n    SELECT 1 FROM archived_battles \n    WHERE player1_user_id = p_user_id OR player2_user_id = p_user_id\n  ) INTO v_has_archived_battles;\n  \n  -- アクティブバトルがある場合、またはアーカイブバトルがある場合はソフト削除\n  IF v_has_active_battles OR v_has_archived_battles THEN\n    \n    -- 進行中のバトルがある場合は強制終了処理\n    IF v_has_active_battles THEN\n      UPDATE active_battles \n      SET status = 'PROCESSING_RESULTS',\n          updated_at = NOW()\n      WHERE (player1_user_id = p_user_id OR player2_user_id = p_user_id)\n        AND status = 'ACTIVE';\n    END IF;\n    \n    -- profilesテーブルをソフト削除（匿名化）\n    UPDATE profiles \n    SET \n      is_deleted = TRUE,\n      deleted_at = NOW(),\n      username = 'deleted-user-' || SUBSTRING(p_user_id::text, 1, 8),\n      email = v_anonymized_email,\n      avatar_url = NULL,\n      bio = 'このアカウントは削除されました',\n      updated_at = NOW()\n    WHERE id = p_user_id;\n    \n    -- 🆕 auth.usersテーブルのメールアドレスも匿名化（重要！）\n    UPDATE auth.users\n    SET \n      email = v_anonymized_email,\n      raw_user_meta_data = COALESCE(raw_user_meta_data, '{}'::jsonb) || \n        jsonb_build_object(\n          'deleted_at', NOW()::text,\n          'original_email_hash', md5(v_original_email)\n        ),\n      updated_at = NOW()\n    WHERE id = p_user_id;\n    \n    RETURN json_build_object(\n      'success', true, \n      'method', 'soft_delete',\n      'reason', CASE \n        WHEN v_has_active_battles THEN 'User has active battles'\n        ELSE 'User has battle history'\n      END,\n      'original_username', v_username,\n      'original_email', v_original_email,\n      'anonymized_email', v_anonymized_email,\n      'email_freed_for_reuse', true\n    );\n    \n  ELSE\n    \n    -- バトル履歴もない場合は物理削除を試行\n    BEGIN\n      -- 関連データを安全な順序で削除\n      DELETE FROM notifications WHERE user_id = p_user_id;\n      DELETE FROM comments WHERE user_id = p_user_id;\n      DELETE FROM posts WHERE user_id = p_user_id;\n      DELETE FROM battle_votes WHERE user_id = p_user_id;\n      DELETE FROM submissions WHERE user_id = p_user_id;\n      \n      -- プロフィール削除\n      DELETE FROM profiles WHERE id = p_user_id;\n      \n      -- 🆕 auth.usersも物理削除（メールアドレス完全解放）\n      DELETE FROM auth.users WHERE id = p_user_id;\n      \n      RETURN json_build_object(\n        'success', true, \n        'method', 'physical_delete',\n        'reason', 'No battle history found',\n        'original_username', v_username,\n        'original_email', v_original_email,\n        'email_freed_for_reuse', true\n      );\n      \n    EXCEPTION WHEN foreign_key_violation THEN\n      -- 外部キー制約エラーの場合はソフト削除にフォールバック\n      UPDATE profiles \n      SET \n        is_deleted = TRUE,\n        deleted_at = NOW(),\n        username = 'deleted-user-' || SUBSTRING(p_user_id::text, 1, 8),\n        email = v_anonymized_email,\n        avatar_url = NULL,\n        bio = 'このアカウントは削除されました',\n        updated_at = NOW()\n      WHERE id = p_user_id;\n      \n      -- auth.usersも匿名化\n      UPDATE auth.users\n      SET \n        email = v_anonymized_email,\n        raw_user_meta_data = COALESCE(raw_user_meta_data, '{}'::jsonb) || \n          jsonb_build_object(\n            'deleted_at', NOW()::text,\n            'original_email_hash', md5(v_original_email)\n          ),\n        updated_at = NOW()\n      WHERE id = p_user_id;\n      \n      RETURN json_build_object(\n        'success', true, \n        'method', 'soft_delete_fallback',\n        'reason', 'Foreign key constraints detected',\n        'original_username', v_username,\n        'original_email', v_original_email,\n        'anonymized_email', v_anonymized_email,\n        'email_freed_for_reuse', true\n      );\n    END;\n    \n  END IF;\n  \nEXCEPTION WHEN OTHERS THEN\n  RETURN json_build_object(\n    'success', false, \n    'error', SQLERRM,\n    'error_detail', SQLSTATE\n  );\nEND;\n$$;\n\n-- 2. 既に削除済みのユーザーのauth.usersメールアドレスを匿名化\nUPDATE auth.users \nSET \n  email = 'deleted-' || SUBSTRING(id::text, 1, 8) || '@deleted.local',\n  raw_user_meta_data = COALESCE(raw_user_meta_data, '{}'::jsonb) || \n    jsonb_build_object(\n      'anonymized_at', NOW()::text,\n      'reason', 'retroactive_email_anonymization'\n    ),\n  updated_at = NOW()\nWHERE id IN (\n  SELECT id FROM profiles WHERE is_deleted = TRUE\n);\n\n-- 3. メールアドレス復旧機能（万が一必要な場合）\nCREATE OR REPLACE FUNCTION get_original_email_hint(p_user_id UUID)\nRETURNS TEXT LANGUAGE plpgsql SECURITY DEFINER AS $$\nDECLARE\n  v_hash TEXT;\nBEGIN\n  -- 管理者やサポート用：元メールのハッシュのみ返す\n  SELECT raw_user_meta_data->>'original_email_hash' INTO v_hash\n  FROM auth.users \n  WHERE id = p_user_id;\n  \n  RETURN v_hash;\nEND;\n$$;\n\n-- 4. コメント追加\nCOMMENT ON FUNCTION safe_delete_user_account(UUID) IS '改良版：auth.usersのメールアドレスも匿名化し、メールアドレスの再利用を可能にする安全な削除関数。';\nCOMMENT ON FUNCTION get_original_email_hint(UUID) IS 'サポート用：削除されたユーザーの元メールアドレスのハッシュを取得（復旧時の確認用）。';"}	fix_email_reuse	rikujiogino@gmail.com	\N
20250612112321	{"-- 20250112120300_update_ranking_views.sql\n-- ランキングビューの更新：削除されたユーザーを除外\n\n-- 1. レーティングランキングビューの更新\nDROP VIEW IF EXISTS rankings_view;\n\nCREATE VIEW rankings_view AS\nWITH battle_stats AS (\n  -- 勝利したバトル\n  SELECT \n    winner_id AS user_id,\n    COUNT(*) AS battles_won\n  FROM archived_battles\n  WHERE winner_id IS NOT NULL\n  GROUP BY winner_id\n  \n  UNION ALL\n  \n  -- 敗北したバトル（プレイヤー1）\n  SELECT \n    player1_user_id AS user_id,\n    0 AS battles_won\n  FROM archived_battles\n  WHERE winner_id != player1_user_id OR winner_id IS NULL\n  \n  UNION ALL\n  \n  -- 敗北したバトル（プレイヤー2）\n  SELECT \n    player2_user_id AS user_id,\n    0 AS battles_won\n  FROM archived_battles\n  WHERE winner_id != player2_user_id OR winner_id IS NULL\n),\naggregated_stats AS (\n  SELECT \n    user_id,\n    SUM(battles_won) AS battles_won,\n    COUNT(*)::numeric - SUM(battles_won) AS battles_lost\n  FROM battle_stats\n  GROUP BY user_id\n)\nSELECT \n  p.id AS user_id,\n  p.username,\n  p.avatar_url,\n  p.rating,\n  p.rating AS season_points,\n  get_rank_from_rating(p.rating) AS rank_name,\n  get_rank_color_from_rating(p.rating) AS rank_color,\n  COALESCE(s.battles_won, 0)::numeric AS battles_won,\n  COALESCE(s.battles_lost, 0)::numeric AS battles_lost,\n  CASE \n    WHEN (COALESCE(s.battles_won, 0) + COALESCE(s.battles_lost, 0)) > 0\n    THEN ROUND((COALESCE(s.battles_won, 0)::numeric / (COALESCE(s.battles_won, 0) + COALESCE(s.battles_lost, 0))) * 100, 1)\n    ELSE 0\n  END AS win_rate,\n  ROW_NUMBER() OVER (ORDER BY p.rating DESC) AS position\nFROM profiles p\nLEFT JOIN aggregated_stats s ON p.id = s.user_id\nWHERE p.is_deleted IS NOT TRUE  -- 🆕 削除されたユーザーを除外\nORDER BY p.rating DESC;\n\n-- 2. 投票者ランキングビューの更新\nDROP VIEW IF EXISTS voter_rankings_view;\n\nCREATE VIEW voter_rankings_view AS\nSELECT \n  p.id AS user_id,\n  p.username,\n  p.avatar_url,\n  p.vote_count,\n  p.rating,\n  get_rank_from_rating(p.rating) AS rank_name,\n  get_rank_color_from_rating(p.rating) AS rank_color,\n  p.created_at,\n  p.updated_at,\n  ROW_NUMBER() OVER (ORDER BY p.vote_count DESC, p.username) AS position\nFROM profiles p\nWHERE p.vote_count > 0 \n  AND p.is_deleted IS NOT TRUE  -- 🆕 削除されたユーザーを除外\nORDER BY p.vote_count DESC, p.username;\n\n-- 3. コメント追加\nCOMMENT ON VIEW rankings_view IS '削除されたユーザーを除外したレーティングベースランキング。is_deleted=TRUEのユーザーは表示されない。';\nCOMMENT ON VIEW voter_rankings_view IS '削除されたユーザーを除外した投票数ベースランキング。is_deleted=TRUEのユーザーは表示されない。';\n\n-- 4. 確認用クエリ\nSELECT \n  'rankings_view updated' as status,\n  COUNT(*) as total_active_users\nFROM rankings_view;\n\nSELECT \n  'voter_rankings_view updated' as status,\n  COUNT(*) as total_active_voters\nFROM voter_rankings_view;"}	update_ranking_views	rikujiogino@gmail.com	\N
20250613035312	{"-- 🔄 自動メールアドレス解放システム + 動画データ完全削除\n-- ソフト削除によるメールアドレス再利用問題の根本的解決 + ストレージクリーンアップ\n\n-- ================================================================\n-- 解決方針:\n-- 1. 削除時に即座にメールアドレスを一意なタイムスタンプ付きで匿名化\n-- 2. 削除されたアカウントに紐づく動画データをSupabase Storageから完全削除\n-- 3. バトルアーカイブは動画なしでも閲覧可能に（フロントエンド側でエラーハンドリング）\n-- 4. 定期的な自動解放処理でさらに古いメールアドレスをクリーンアップ\n-- ================================================================\n\n-- Supabase Storageから動画ファイルを削除する関数\nCREATE OR REPLACE FUNCTION delete_user_videos_from_storage(p_user_id uuid)\nRETURNS JSON\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_video_record RECORD;\n  v_deleted_count INTEGER := 0;\n  v_failed_count INTEGER := 0;\n  v_deleted_urls TEXT[] := '{}';\n  v_failed_urls TEXT[] := '{}';\n  v_storage_path TEXT;\nBEGIN\n  -- ユーザーに関連する全ての動画URLを取得\n  -- submissions, archived_battlesから動画URLを収集\n  FOR v_video_record IN\n    -- submissionsテーブルから\n    SELECT video_url, 'submissions' as source_table\n    FROM submissions \n    WHERE user_id = p_user_id\n      AND video_url IS NOT NULL\n    UNION\n    -- archived_battlesのplayer1_video_url\n    SELECT player1_video_url as video_url, 'archived_battles_p1' as source_table\n    FROM archived_battles \n    WHERE player1_user_id = p_user_id\n      AND player1_video_url IS NOT NULL\n    UNION\n    -- archived_battlesのplayer2_video_url  \n    SELECT player2_video_url as video_url, 'archived_battles_p2' as source_table\n    FROM archived_battles \n    WHERE player2_user_id = p_user_id\n      AND player2_video_url IS NOT NULL\n  LOOP\n    BEGIN\n      -- Supabase Storage URLからファイルパスを抽出\n      -- 例: https://xxx.supabase.co/storage/v1/object/public/videos/path/to/file.mp4\n      -- -> videos/path/to/file.mp4\n      v_storage_path := regexp_replace(\n        v_video_record.video_url, \n        '.*\\\\/storage\\\\/v1\\\\/object\\\\/public\\\\/([^?]+)(\\\\?.*)?$', \n        '\\\\1'\n      );\n      \n      -- Storageから物理削除\n      -- storage.objects テーブルから直接削除\n      DELETE FROM storage.objects \n      WHERE bucket_id = 'videos' \n        AND name = replace(v_storage_path, 'videos/', '');\n      \n      IF FOUND THEN\n        v_deleted_count := v_deleted_count + 1;\n        v_deleted_urls := v_deleted_urls || v_video_record.video_url;\n      ELSE\n        v_failed_count := v_failed_count + 1;\n        v_failed_urls := v_failed_urls || v_video_record.video_url;\n      END IF;\n      \n    EXCEPTION WHEN OTHERS THEN\n      v_failed_count := v_failed_count + 1;\n      v_failed_urls := v_failed_urls || v_video_record.video_url;\n      -- エラーログは出力するが処理は継続\n      RAISE NOTICE 'Failed to delete video: %, Error: %', v_video_record.video_url, SQLERRM;\n    END;\n  END LOOP;\n  \n  RETURN json_build_object(\n    'success', true,\n    'deleted_count', v_deleted_count,\n    'failed_count', v_failed_count,\n    'deleted_urls', v_deleted_urls,\n    'failed_urls', v_failed_urls,\n    'user_id', p_user_id\n  );\nEND;\n$$;\n\n-- 改良版削除関数（即座メールアドレス解放 + 動画削除）\nCREATE OR REPLACE FUNCTION safe_delete_user_account_v2(p_user_id uuid)\nRETURNS json\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_has_active_battles BOOLEAN := FALSE;\n  v_has_archived_battles BOOLEAN := FALSE;\n  v_username TEXT;\n  v_original_email TEXT;\n  v_anonymized_email TEXT;\n  v_timestamp BIGINT;\n  v_video_deletion_result JSON;\nBEGIN\n  -- 現在のユーザー名とメールアドレスを取得\n  SELECT username INTO v_username FROM profiles WHERE id = p_user_id;\n  SELECT email INTO v_original_email FROM auth.users WHERE id = p_user_id;\n  \n  IF NOT FOUND THEN\n    RETURN json_build_object(\n      'success', false,\n      'error', 'User not found'\n    );\n  END IF;\n\n  -- タイムスタンプ付きの一意な匿名化メールアドレスを生成\n  v_timestamp := EXTRACT(EPOCH FROM NOW())::BIGINT;\n  v_anonymized_email := 'deleted-' || SUBSTRING(p_user_id::text, 1, 8) || '-' || v_timestamp || '@deleted.local';\n\n  -- 🎬 ユーザーの動画データを全て削除\n  BEGIN\n    SELECT delete_user_videos_from_storage(p_user_id) INTO v_video_deletion_result;\n  EXCEPTION WHEN OTHERS THEN\n    -- 動画削除に失敗してもアカウント削除は継続\n    v_video_deletion_result := json_build_object(\n      'success', false,\n      'error', SQLERRM,\n      'deleted_count', 0,\n      'failed_count', 0\n    );\n  END;\n\n  -- アクティブバトルの存在確認\n  SELECT EXISTS(\n    SELECT 1 FROM active_battles \n    WHERE player1_user_id = p_user_id OR player2_user_id = p_user_id\n  ) INTO v_has_active_battles;\n  \n  -- アーカイブバトルの存在確認\n  SELECT EXISTS(\n    SELECT 1 FROM archived_battles \n    WHERE player1_user_id = p_user_id OR player2_user_id = p_user_id\n  ) INTO v_has_archived_battles;\n  \n  -- アクティブバトルまたはアーカイブバトルがある場合はソフト削除\n  IF v_has_active_battles OR v_has_archived_battles THEN\n    \n    -- 進行中のバトルがある場合は強制終了処理\n    IF v_has_active_battles THEN\n      UPDATE active_battles \n      SET status = 'PROCESSING_RESULTS',\n          updated_at = NOW()\n      WHERE (player1_user_id = p_user_id OR player2_user_id = p_user_id)\n        AND status = 'ACTIVE';\n    END IF;\n    \n    -- profilesテーブルをソフト削除（匿名化）\n    UPDATE profiles \n    SET \n      is_deleted = TRUE,\n      deleted_at = NOW(),\n      username = 'deleted-user-' || SUBSTRING(p_user_id::text, 1, 8),\n      email = v_anonymized_email,\n      avatar_url = NULL,\n      bio = 'このアカウントは削除されました',\n      updated_at = NOW()\n    WHERE id = p_user_id;\n    \n    -- 🆕 auth.usersテーブルのメールアドレスを即座に一意な形式で匿名化\n    UPDATE auth.users\n    SET \n      email = v_anonymized_email,\n      raw_user_meta_data = COALESCE(raw_user_meta_data, '{}'::jsonb) || \n        jsonb_build_object(\n          'deleted_at', NOW()::text,\n          'original_email_hash', md5(v_original_email),\n          'email_immediately_released', true,\n          'deletion_timestamp', v_timestamp,\n          'deletion_method', 'soft_delete_with_immediate_email_release',\n          'videos_deleted', v_video_deletion_result\n        ),\n      updated_at = NOW()\n    WHERE id = p_user_id;\n    \n    RETURN json_build_object(\n      'success', true, \n      'method', 'soft_delete_with_immediate_release',\n      'reason', CASE \n        WHEN v_has_active_battles THEN 'User has active battles'\n        ELSE 'User has battle history'\n      END,\n      'original_username', v_username,\n      'original_email', v_original_email,\n      'anonymized_email', v_anonymized_email,\n      'email_freed_for_reuse', true,\n      'timestamp', v_timestamp,\n      'video_cleanup', v_video_deletion_result\n    );\n    \n  ELSE\n    -- バトル履歴がない場合は物理削除（改良版）\n    \n    -- 🎬 動画削除の実行\n    -- 削除前に動画ファイルをクリーンアップ\n    SELECT delete_user_videos_from_storage(p_user_id) INTO v_video_deletion_result;\n    \n    -- 関連データを全て削除\n    DELETE FROM battle_votes WHERE user_id = p_user_id;\n    DELETE FROM notifications WHERE user_id = p_user_id;\n    DELETE FROM submissions WHERE user_id = p_user_id;\n    DELETE FROM posts WHERE user_id = p_user_id;\n    DELETE FROM comments WHERE user_id = p_user_id;\n    DELETE FROM profiles WHERE id = p_user_id;\n    \n    -- auth.usersからも完全削除\n    DELETE FROM auth.users WHERE id = p_user_id;\n    \n    RETURN json_build_object(\n      'success', true, \n      'method', 'physical_delete_with_video_cleanup',\n      'reason', 'No battle history found',\n      'original_username', v_username,\n      'original_email', v_original_email,\n      'email_freed_for_reuse', true,\n      'video_cleanup', v_video_deletion_result\n    );\n  END IF;\nEND;\n$$;\n\n-- 定期的にメールアドレスを自動解放する関数\n-- 古い形式（deleted@example.com）のメールアドレスを新しい一意形式に変換\nCREATE OR REPLACE FUNCTION auto_release_deleted_emails()\nRETURNS json\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_updated_count INTEGER := 0;\n  v_user_record RECORD;\n  v_timestamp BIGINT;\n  v_new_email TEXT;\nBEGIN\n  -- deleted@example.com 形式のメールアドレスを一意な形式に変換\n  FOR v_user_record IN\n    SELECT id, email \n    FROM auth.users \n    WHERE email = 'deleted@example.com'\n      AND (raw_user_meta_data->>'email_immediately_released')::boolean IS NOT TRUE\n    LIMIT 100  -- 一度に処理する件数を制限\n  LOOP\n    -- タイムスタンプ付きの新しいメールアドレスを生成\n    v_timestamp := EXTRACT(EPOCH FROM NOW())::BIGINT;\n    v_new_email := 'deleted-' || SUBSTRING(v_user_record.id::text, 1, 8) || '-' || v_timestamp || '@deleted.local';\n    \n    -- auth.users テーブルを更新\n    UPDATE auth.users\n    SET \n      email = v_new_email,\n      raw_user_meta_data = COALESCE(raw_user_meta_data, '{}'::jsonb) || \n        jsonb_build_object(\n          'email_auto_released_at', NOW()::text,\n          'email_immediately_released', true,\n          'auto_release_timestamp', v_timestamp,\n          'previous_email', v_user_record.email\n        ),\n      updated_at = NOW()\n    WHERE id = v_user_record.id;\n    \n    -- profilesテーブルも更新\n    UPDATE profiles\n    SET \n      email = v_new_email,\n      updated_at = NOW()\n    WHERE id = v_user_record.id;\n    \n    v_updated_count := v_updated_count + 1;\n  END LOOP;\n  \n  RETURN json_build_object(\n    'success', true,\n    'updated_count', v_updated_count,\n    'message', 'Email addresses auto-released for reuse'\n  );\nEND;\n$$;\n\n-- 削除されたユーザーの動画を一括クリーンアップする関数\nCREATE OR REPLACE FUNCTION cleanup_all_deleted_user_videos()\nRETURNS json\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_deleted_user RECORD;\n  v_cleanup_result JSON;\n  v_total_videos_deleted INTEGER := 0;\n  v_total_videos_failed INTEGER := 0;\n  v_processed_users INTEGER := 0;\nBEGIN\n  -- 削除済みユーザーをループ処理\n  FOR v_deleted_user IN\n    SELECT id \n    FROM profiles \n    WHERE is_deleted = TRUE\n    LIMIT 50  -- 一度に処理するユーザー数を制限\n  LOOP\n    -- 各ユーザーの動画を削除\n    SELECT cleanup_all_deleted_user_videos() INTO v_cleanup_result;\n    \n    -- 結果を集計\n    v_total_videos_deleted := v_total_videos_deleted + COALESCE((v_cleanup_result->>'deleted_count')::INTEGER, 0);\n    v_total_videos_failed := v_total_videos_failed + COALESCE((v_cleanup_result->>'failed_count')::INTEGER, 0);\n    v_processed_users := v_processed_users + 1;\n    \n  END LOOP;\n  \n  RETURN json_build_object(\n    'success', true,\n    'processed_users', v_processed_users,\n    'total_videos_deleted', v_total_videos_deleted,\n    'total_videos_failed', v_total_videos_failed,\n    'message', 'Bulk video cleanup completed'\n  );\nEND;\n$$;\n\n-- 旧関数をv2に置き換え（後方互換性のため）\nCREATE OR REPLACE FUNCTION safe_delete_user_account(p_user_id uuid)\nRETURNS json\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nBEGIN\n  -- 新しいv2関数を呼び出し\n  RETURN safe_delete_user_account_v2(p_user_id);\nEND;\n$$;"}	implement_auto_email_release_with_video_cleanup	rikujiogino@gmail.com	\N
20250613040748	{"-- 🎯 理想的な段階的マッチングシステムの実装\n-- ユーザー提案の時間ベース許容レート差システム\n\n-- 改良版 progressive_matchmaking 関数\nCREATE OR REPLACE FUNCTION progressive_matchmaking()\nRETURNS json\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_submission_rec RECORD;\n  v_opponent_rec RECORD;\n  v_battle_id UUID;\n  v_voting_end_time TIMESTAMPTZ;\n  v_processed_count INTEGER := 0;\n  v_matched_count INTEGER := 0;\n  v_results JSON[] := '{}';\n  v_match_result JSON;\n  v_rating_tolerance INTEGER;\n  v_waiting_hours NUMERIC;\n  v_submitter_rating INTEGER;\n  v_opponent_rating INTEGER;\nBEGIN\n  -- 初期待機期間を10分に設定（即座マッチングの猶予期間）\n  FOR v_submission_rec IN\n    SELECT \n      s.id,\n      s.user_id,\n      s.created_at,\n      s.battle_format,\n      s.video_url,\n      p.rating,\n      EXTRACT(EPOCH FROM (NOW() - s.created_at)) / 3600 as waiting_hours\n    FROM public.submissions s\n    JOIN public.profiles p ON s.user_id = p.id\n    WHERE s.status = 'WAITING_OPPONENT'\n      AND s.created_at + INTERVAL '10 minutes' <= NOW()  -- 初期待機10分\n    ORDER BY s.created_at ASC\n  LOOP\n    v_processed_count := v_processed_count + 1;\n    v_waiting_hours := v_submission_rec.waiting_hours;\n    v_submitter_rating := v_submission_rec.rating;\n    \n    -- 🎯 理想的な時間ベース許容レート差システム\n    IF v_waiting_hours < 6 THEN\n      v_rating_tolerance := 50;   -- 0-6時間: ±50（新鮮な対戦はほぼ同格同士）\n    ELSIF v_waiting_hours < 24 THEN\n      v_rating_tolerance := 100;  -- 6-24時間: ±100（少し幅を持たせてマッチ確率UP）\n    ELSIF v_waiting_hours < 72 THEN\n      v_rating_tolerance := 200;  -- 24-72時間: ±200（24時間以内にマッチできなかったら緩和）\n    ELSIF v_waiting_hours < 168 THEN\n      v_rating_tolerance := 300;  -- 72-168時間: ±300（3日-7日経過でさらに緩和）\n    ELSE\n      v_rating_tolerance := 999999; -- 168時間（7日）以降: 無制限（どうしても当たらない場合は全体からマッチ）\n    END IF;\n    \n    -- 対戦相手を検索（同じ初期待機期間を満たしている相手のみ）\n    SELECT \n      s2.id,\n      s2.user_id,\n      s2.created_at,\n      s2.video_url,\n      p2.rating\n    INTO v_opponent_rec\n    FROM public.submissions s2\n    JOIN public.profiles p2 ON s2.user_id = p2.id\n    WHERE s2.status = 'WAITING_OPPONENT'\n      AND s2.id != v_submission_rec.id\n      AND s2.user_id != v_submission_rec.user_id\n      AND s2.battle_format = v_submission_rec.battle_format\n      AND s2.created_at + INTERVAL '10 minutes' <= NOW()  -- 相手も10分間待機済み\n      AND ABS(p2.rating - v_submitter_rating) <= v_rating_tolerance\n    ORDER BY ABS(p2.rating - v_submitter_rating) ASC, s2.created_at ASC\n    LIMIT 1;\n    \n    -- マッチした場合はバトルを作成\n    IF FOUND THEN\n      -- バトル作成\n      v_battle_id := gen_random_uuid();\n      v_voting_end_time := NOW() + INTERVAL '5 days';\n      \n      -- active_battles テーブルに挿入\n      INSERT INTO public.active_battles (\n        id,\n        player1_submission_id,\n        player2_submission_id,\n        player1_user_id,\n        player2_user_id,\n        battle_format,\n        status,\n        votes_a,\n        votes_b,\n        end_voting_at,\n        created_at,\n        updated_at\n      ) VALUES (\n        v_battle_id,\n        v_submission_rec.id,\n        v_opponent_rec.id,\n        v_submission_rec.user_id,\n        v_opponent_rec.user_id,\n        v_submission_rec.battle_format,\n        'ACTIVE',\n        0,\n        0,\n        v_voting_end_time,\n        NOW(),\n        NOW()\n      );\n      \n      -- 両方の投稿ステータスを更新\n      UPDATE public.submissions \n      SET \n        status = 'MATCHED_IN_BATTLE',\n        active_battle_id = v_battle_id,\n        updated_at = NOW()\n      WHERE id IN (v_submission_rec.id, v_opponent_rec.id);\n      \n      v_matched_count := v_matched_count + 1;\n      \n      -- マッチング結果を記録\n      v_match_result := json_build_object(\n        'submission_id', v_submission_rec.id,\n        'opponent_id', v_opponent_rec.id,\n        'battle_id', v_battle_id,\n        'submitter_rating', v_submitter_rating,\n        'opponent_rating', v_opponent_rec.rating,\n        'rating_difference', ABS(v_submitter_rating - v_opponent_rec.rating),\n        'waiting_hours', ROUND(v_waiting_hours, 2),\n        'rating_tolerance_used', v_rating_tolerance,\n        'matched', true,\n        'match_type', 'progressive_ideal_time_based',\n        'voting_period_days', 5\n      );\n      \n      v_results := v_results || v_match_result;\n      \n      RAISE NOTICE 'Ideal progressive match created: % vs % (rating diff: %, waited: % hours, tolerance: ±%)', \n        v_submission_rec.id, v_opponent_rec.id, \n        ABS(v_submitter_rating - v_opponent_rec.rating), ROUND(v_waiting_hours, 2), v_rating_tolerance;\n        \n    ELSE\n      -- マッチしなかった場合の記録\n      v_match_result := json_build_object(\n        'submission_id', v_submission_rec.id,\n        'submitter_rating', v_submitter_rating,\n        'waiting_hours', ROUND(v_waiting_hours, 2),\n        'rating_tolerance_used', v_rating_tolerance,\n        'matched', false,\n        'reason', 'No suitable opponent found'\n      );\n      \n      v_results := v_results || v_match_result;\n    END IF;\n  END LOOP;\n  \n  RETURN json_build_object(\n    'processed_submissions', v_processed_count,\n    'matches_created', v_matched_count,\n    'results', v_results,\n    'timestamp', NOW(),\n    'function_version', 'v5_ideal_time_based_matching',\n    'execution_interval', '30_minutes',\n    'initial_wait_period', '10_minutes',\n    'rating_tolerance_schedule', json_build_object(\n      '0_to_6_hours', 50,\n      '6_to_24_hours', 100,\n      '24_to_72_hours', 200,\n      '72_to_168_hours', 300,\n      '168_hours_plus', 'unlimited'\n    )\n  );\n  \nEXCEPTION WHEN OTHERS THEN\n  RAISE NOTICE 'Error in progressive_matchmaking: %', SQLERRM;\n  RETURN json_build_object(\n    'error', SQLERRM,\n    'processed_submissions', v_processed_count,\n    'matches_created', v_matched_count,\n    'timestamp', NOW(),\n    'function_version', 'v5_ideal_time_based_matching'\n  );\nEND;\n$$;\n\n-- 関数の説明を追加\nCOMMENT ON FUNCTION progressive_matchmaking() IS '\n理想的な時間ベース段階的マッチングシステム:\n- 0-6時間: ±50レート差（新鮮な対戦はほぼ同格同士）\n- 6-24時間: ±100レート差（少し幅を持たせてマッチ確率UP）\n- 24-72時間: ±200レート差（24時間以内にマッチできなかったら緩和）\n- 72-168時間: ±300レート差（3日-7日経過でさらに緩和）\n- 168時間以降: 無制限（どうしても当たらない場合は全体からマッチ）\n';\n\n-- 実行ログ\nSELECT 'ℹ️ 理想的な段階的マッチングシステムが実装されました' as message;\nSELECT '📊 時間ベース許容レート差: 6h(±50) → 24h(±100) → 72h(±200) → 168h(±300) → 無制限' as schedule;\nSELECT '⏰ 実行間隔: 30分ごと、初期待機: 10分間' as timing;"}	implement_ideal_progressive_matchmaking	rikujiogino@gmail.com	\N
20250613041739	{"-- 完全なメールアドレス解放システムの実装\n-- 削除後すぐに同じメールアドレスで再登録可能にする\n\n-- 1. 既存のソフト削除されたユーザーのメールアドレスを完全に解放\nUPDATE auth.users\nSET \n  email = 'permanently-deleted-' || EXTRACT(EPOCH FROM NOW())::BIGINT || '-' || SUBSTRING(id::text, 1, 8) || '@void.deleted',\n  raw_user_meta_data = jsonb_build_object(\n    'permanently_deleted', true,\n    'deletion_timestamp', EXTRACT(EPOCH FROM NOW())::BIGINT,\n    'original_email_permanently_released', true,\n    'deletion_method', 'retroactive_complete_cleanup'\n  ),\n  updated_at = NOW()\nWHERE id IN (\n  SELECT id FROM profiles WHERE is_deleted = TRUE\n);\n\n-- 2. 改良されたアカウント削除関数（完全なメールアドレス解放版）\nCREATE OR REPLACE FUNCTION safe_delete_user_account_v3(p_user_id UUID)\nRETURNS JSON\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_has_active_battles BOOLEAN := FALSE;\n  v_has_archived_battles BOOLEAN := FALSE;\n  v_username TEXT;\n  v_original_email TEXT;\n  v_permanently_anonymized_email TEXT;\n  v_timestamp BIGINT;\n  v_video_deletion_result JSON;\nBEGIN\n  -- 現在のユーザー名とメールアドレスを取得\n  SELECT username INTO v_username FROM profiles WHERE id = p_user_id;\n  SELECT email INTO v_original_email FROM auth.users WHERE id = p_user_id;\n  \n  IF NOT FOUND THEN\n    RETURN json_build_object(\n      'success', false,\n      'error', 'User not found'\n    );\n  END IF;\n\n  -- タイムスタンプ付きの完全に一意な匿名化メールアドレスを生成\n  v_timestamp := EXTRACT(EPOCH FROM NOW())::BIGINT;\n  v_permanently_anonymized_email := 'permanently-deleted-' || v_timestamp || '-' || SUBSTRING(p_user_id::text, 1, 8) || '@void.deleted';\n\n  -- 🎬 ユーザーの動画データを全て削除\n  BEGIN\n    SELECT delete_user_videos_from_storage(p_user_id) INTO v_video_deletion_result;\n  EXCEPTION WHEN OTHERS THEN\n    -- 動画削除に失敗してもアカウント削除は継続\n    v_video_deletion_result := json_build_object(\n      'success', false,\n      'error', SQLERRM,\n      'deleted_count', 0,\n      'failed_count', 0\n    );\n  END;\n\n  -- アクティブバトルの存在確認\n  SELECT EXISTS(\n    SELECT 1 FROM active_battles \n    WHERE player1_user_id = p_user_id OR player2_user_id = p_user_id\n  ) INTO v_has_active_battles;\n  \n  -- アーカイブバトルの存在確認\n  SELECT EXISTS(\n    SELECT 1 FROM archived_battles \n    WHERE player1_user_id = p_user_id OR player2_user_id = p_user_id\n  ) INTO v_has_archived_battles;\n  \n  -- アクティブバトルまたはアーカイブバトルがある場合はソフト削除（完全メール解放版）\n  IF v_has_active_battles OR v_has_archived_battles THEN\n    \n    -- 進行中のバトルがある場合は強制終了処理\n    IF v_has_active_battles THEN\n      UPDATE active_battles \n      SET status = 'PROCESSING_RESULTS',\n          updated_at = NOW()\n      WHERE (player1_user_id = p_user_id OR player2_user_id = p_user_id)\n        AND status = 'ACTIVE';\n    END IF;\n    \n    -- profilesテーブルをソフト削除（匿名化）\n    UPDATE profiles \n    SET \n      is_deleted = TRUE,\n      deleted_at = NOW(),\n      username = 'deleted-user-' || SUBSTRING(p_user_id::text, 1, 8),\n      email = v_permanently_anonymized_email,\n      avatar_url = NULL,\n      bio = 'このアカウントは削除されました',\n      updated_at = NOW()\n    WHERE id = p_user_id;\n    \n    -- 🆕 auth.usersテーブルのメールアドレスを完全に匿名化（元のメール情報も完全削除）\n    UPDATE auth.users\n    SET \n      email = v_permanently_anonymized_email,\n      raw_user_meta_data = jsonb_build_object(\n        'permanently_deleted', true,\n        'deletion_timestamp', v_timestamp,\n        'original_email_permanently_released', true,\n        'deletion_method', 'soft_delete_with_complete_email_release',\n        'videos_deleted', v_video_deletion_result\n      ),\n      updated_at = NOW()\n    WHERE id = p_user_id;\n    \n    RETURN json_build_object(\n      'success', true, \n      'method', 'soft_delete_with_complete_email_release',\n      'reason', CASE \n        WHEN v_has_active_battles THEN 'User has active battles'\n        ELSE 'User has battle history'\n      END,\n      'original_username', v_username,\n      'original_email_completely_released', true,\n      'email_available_for_immediate_reuse', true,\n      'timestamp', v_timestamp,\n      'video_cleanup', v_video_deletion_result\n    );\n    \n  ELSE\n    -- バトル履歴がない場合は物理削除（完全削除版）\n    \n    -- 関連データを全て削除\n    DELETE FROM battle_votes WHERE user_id = p_user_id;\n    DELETE FROM notifications WHERE user_id = p_user_id;\n    DELETE FROM submissions WHERE user_id = p_user_id;\n    DELETE FROM posts WHERE user_id = p_user_id;\n    DELETE FROM comments WHERE user_id = p_user_id;\n    DELETE FROM profiles WHERE id = p_user_id;\n    \n    -- auth.usersからも完全削除\n    DELETE FROM auth.users WHERE id = p_user_id;\n    \n    RETURN json_build_object(\n      'success', true, \n      'method', 'complete_physical_delete',\n      'reason', 'No battle history found',\n      'original_username', v_username,\n      'original_email_completely_released', true,\n      'email_available_for_immediate_reuse', true,\n      'video_cleanup', v_video_deletion_result\n    );\n  END IF;\nEND;\n$$;\n\n-- 3. メイン削除関数をv3に更新\nCREATE OR REPLACE FUNCTION safe_delete_user_account(p_user_id UUID)\nRETURNS JSON\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nBEGIN\n  -- 新しいv3関数を呼び出し\n  RETURN safe_delete_user_account_v3(p_user_id);\nEND;\n$$;\n\n-- 4. 管理者用：特定のメールアドレスを強制的に解放する関数\nCREATE OR REPLACE FUNCTION admin_force_release_email(p_email TEXT)\nRETURNS JSON\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  v_user_count INTEGER;\n  v_timestamp BIGINT;\nBEGIN\n  -- 指定されたメールアドレスを使用しているユーザー数を確認\n  SELECT COUNT(*) INTO v_user_count\n  FROM auth.users \n  WHERE email = p_email;\n  \n  IF v_user_count = 0 THEN\n    RETURN json_build_object(\n      'success', true,\n      'message', 'Email address is already available',\n      'email', p_email\n    );\n  END IF;\n  \n  v_timestamp := EXTRACT(EPOCH FROM NOW())::BIGINT;\n  \n  -- 該当するauth.usersレコードを完全に匿名化\n  UPDATE auth.users\n  SET \n    email = 'force-released-' || v_timestamp || '-' || SUBSTRING(id::text, 1, 8) || '@admin.released',\n    raw_user_meta_data = jsonb_build_object(\n      'admin_force_released', true,\n      'release_timestamp', v_timestamp,\n      'original_email_force