20250625043650	{"-- 🛎️ Push Notification System - Push Subscriptions テーブル作成\n-- 各ユーザーの Web Push 購読情報を保存\n\n-- Push 購読情報テーブル\nCREATE TABLE public.push_subscriptions (\n  id uuid PRIMARY KEY DEFAULT uuid_generate_v4(),\n  user_id uuid REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,\n  subscription jsonb NOT NULL, -- { endpoint, keys: { p256dh, auth } }\n  user_agent text, -- デバッグ用：どのブラウザ/デバイスか\n  created_at timestamptz DEFAULT now() NOT NULL,\n  updated_at timestamptz DEFAULT now() NOT NULL\n);\n\n-- ユニーク制約：同じユーザー・同じエンドポイントの重複を防ぐ\nCREATE UNIQUE INDEX unique_user_endpoint ON public.push_subscriptions(user_id, (subscription->>'endpoint'));\n\n-- インデックス：ユーザーIDでの高速検索\nCREATE INDEX idx_push_subscriptions_user_id ON public.push_subscriptions(user_id);\n\n-- RLS (Row Level Security) 有効化\nALTER TABLE public.push_subscriptions ENABLE ROW LEVEL SECURITY;\n\n-- ポリシー：ユーザーは自分の購読情報のみアクセス可能\nCREATE POLICY \\"Users can manage their own push subscriptions\\" ON public.push_subscriptions\n  FOR ALL USING (auth.uid() = user_id);\n\n-- 権限付与\nGRANT SELECT, INSERT, UPDATE, DELETE ON public.push_subscriptions TO authenticated;\n\n-- updated_at 自動更新トリガー\nCREATE OR REPLACE FUNCTION update_updated_at_column()\nRETURNS TRIGGER AS $$\nBEGIN\n  NEW.updated_at = now();\n  RETURN NEW;\nEND;\n$$ language 'plpgsql';\n\nCREATE TRIGGER update_push_subscriptions_updated_at \n  BEFORE UPDATE ON public.push_subscriptions \n  FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();\n\n-- コメント\nCOMMENT ON TABLE public.push_subscriptions IS 'Web Push 通知の購読情報を保存';\nCOMMENT ON COLUMN public.push_subscriptions.subscription IS 'ブラウザからの PushSubscription オブジェクト（JSON形式）';\nCOMMENT ON COLUMN public.push_subscriptions.user_agent IS 'デバッグ・統計用のユーザーエージェント情報';"}	create_push_subscriptions_final	rikujiogino@gmail.com	\N
20250625043937	{"-- 🛎️ Push Notification System - Database Triggers\n-- 各イベント発生時にEdge Functionを呼び出すトリガーを設定\n\n-- ヘルパー関数: Edge Function呼び出し用（HTTP拡張を使用）\nCREATE OR REPLACE FUNCTION call_edge_function(\n  function_name text,\n  payload jsonb\n) RETURNS void AS $$\nDECLARE\n  edge_function_url text;\nBEGIN\n  -- Edge Function のURL を構築\n  edge_function_url := 'https://qgqcjtjxaoplhxurbpis.supabase.co/functions/v1/' || function_name;\n\n  -- HTTP POST でEdge Functionを呼び出し（非同期）\n  -- NOTE: http 拡張を使用\n  PERFORM net.http_post(\n    url := edge_function_url,\n    body := payload,\n    headers := jsonb_build_object(\n      'Content-Type', 'application/json',\n      'Authorization', 'Bearer ' || current_setting('request.jwt.claims', true)::jsonb->>'aud'\n    )\n  );\n  \nEXCEPTION WHEN OTHERS THEN\n  -- エラーが発生してもトリガー処理は継続\n  RAISE LOG 'Failed to call edge function %: %', function_name, SQLERRM;\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;\n\n-- 1. バトル成立時の通知トリガー\nCREATE OR REPLACE FUNCTION notify_battle_created_trigger()\nRETURNS trigger AS $$\nBEGIN\n  -- バトルが新しくACTIVEステータスになった場合のみ\n  IF NEW.status = 'ACTIVE' AND (OLD IS NULL OR OLD.status != 'ACTIVE') THEN\n    PERFORM call_edge_function(\n      'notify-battle-created',\n      jsonb_build_object('battle_id', NEW.id)\n    );\n  END IF;\n  \n  RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;\n\n-- active_battlesテーブルにトリガーを設定\nDROP TRIGGER IF EXISTS trigger_notify_battle_created ON active_battles;\nCREATE TRIGGER trigger_notify_battle_created\n  AFTER INSERT OR UPDATE ON active_battles\n  FOR EACH ROW\n  EXECUTE FUNCTION notify_battle_created_trigger();\n\n-- 2. 投票発生時の通知トリガー\nCREATE OR REPLACE FUNCTION notify_vote_cast_trigger()\nRETURNS trigger AS $$\nDECLARE\n  voted_user_id uuid;\nBEGIN\n  -- 投票されたユーザーIDを特定（AかBかによって異なる）\n  IF NEW.vote = 'A' THEN\n    SELECT player1_user_id INTO voted_user_id \n    FROM active_battles \n    WHERE id = NEW.battle_id;\n  ELSE\n    SELECT player2_user_id INTO voted_user_id \n    FROM active_battles \n    WHERE id = NEW.battle_id;\n  END IF;\n  \n  -- 新しい投票が追加された場合\n  PERFORM call_edge_function(\n    'notify-vote-cast',\n    jsonb_build_object(\n      'battle_id', NEW.battle_id,\n      'voter_id', NEW.user_id,\n      'voted_user_id', voted_user_id\n    )\n  );\n  \n  RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;\n\n-- battle_votesテーブルにトリガーを設定\nDROP TRIGGER IF EXISTS trigger_notify_vote_cast ON battle_votes;\nCREATE TRIGGER trigger_notify_vote_cast\n  AFTER INSERT ON battle_votes\n  FOR EACH ROW\n  EXECUTE FUNCTION notify_vote_cast_trigger();\n\n-- 3. バトル結果確定時の通知トリガー\nCREATE OR REPLACE FUNCTION notify_battle_completed_trigger()\nRETURNS trigger AS $$\nBEGIN\n  -- バトルがCOMPLETEDステータスになった場合\n  IF NEW.status = 'COMPLETED' AND (OLD IS NULL OR OLD.status != 'COMPLETED') THEN\n    PERFORM call_edge_function(\n      'notify-battle-completed',\n      jsonb_build_object('battle_id', NEW.id)\n    );\n  END IF;\n  \n  RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER;\n\n-- active_battlesテーブルにバトル完了トリガーを設定\nDROP TRIGGER IF EXISTS trigger_notify_battle_completed ON active_battles;\nCREATE TRIGGER trigger_notify_battle_completed\n  AFTER UPDATE ON active_battles\n  FOR EACH ROW\n  EXECUTE FUNCTION notify_battle_completed_trigger();\n\n-- 権限設定\nGRANT EXECUTE ON FUNCTION call_edge_function(text, jsonb) TO authenticated;\nGRANT EXECUTE ON FUNCTION notify_battle_created_trigger() TO authenticated;\nGRANT EXECUTE ON FUNCTION notify_vote_cast_trigger() TO authenticated;\nGRANT EXECUTE ON FUNCTION notify_battle_completed_trigger() TO authenticated;"}	add_push_triggers_fixed	rikujiogino@gmail.com	\N
20250625050101	{"-- Push Subscriptions テーブルの RLS ポリシーを修正\n\n-- 既存のポリシーを削除\nDROP POLICY IF EXISTS \\"Users can manage their own push subscriptions\\" ON public.push_subscriptions;\n\n-- より簡単なポリシーを作成（認証済みユーザーは自分のレコードにアクセス可能）\nCREATE POLICY \\"authenticated_users_own_subscriptions\\" ON public.push_subscriptions\n  FOR ALL \n  TO authenticated\n  USING (auth.uid() = user_id)\n  WITH CHECK (auth.uid() = user_id);\n\n-- テーブルの権限を再確認\nGRANT ALL ON public.push_subscriptions TO authenticated;\n\n-- シーケンスの権限も確認（UUIDのためCOMMENT追加）\nCOMMENT ON TABLE public.push_subscriptions IS 'Web Push subscriptions with fixed RLS policies';"}	fix_push_subscriptions_rls	rikujiogino@gmail.com	\N
20250626022654	{"/*\n  # 引き分け時レーティング変動システムの実装\n\n  1. 引き分け専用レーティング計算関数の作成\n  2. process_expired_battles関数の更新（引き分け時もレーティング更新）\n  3. 標準Elo方式（引き分け = 0.5の結果）を採用\n*/\n\n-- Step 1: 引き分け時のレーティング計算関数を作成\nCREATE OR REPLACE FUNCTION calculate_tie_rating_with_format(\n  player1_rating INTEGER,\n  player2_rating INTEGER,\n  battle_format TEXT\n)\nRETURNS JSON\nLANGUAGE plpgsql\nAS $$\nDECLARE\n  k_factor INTEGER;\n  player1_change INTEGER;\n  player2_change INTEGER;\n  new_player1_rating INTEGER;\n  new_player2_rating INTEGER;\nBEGIN\n  -- Get K-factor based on battle format\n  k_factor := get_k_factor_by_format(battle_format);\n  \n  -- Calculate rating changes for tie (result = 0.5 for both players)\n  player1_change := calculate_elo_rating_change(player1_rating, player2_rating, 0.5, k_factor);\n  player2_change := calculate_elo_rating_change(player2_rating, player1_rating, 0.5, k_factor);\n  \n  -- Apply changes with minimum rating protection\n  new_player1_rating := GREATEST(player1_rating + player1_change, 1100);\n  new_player2_rating := GREATEST(player2_rating + player2_change, 1100);\n  \n  RETURN json_build_object(\n    'player1_rating', new_player1_rating,\n    'player2_rating', new_player2_rating,\n    'player1_change', new_player1_rating - player1_rating,\n    'player2_change', new_player2_rating - player2_rating,\n    'k_factor_used', k_factor,\n    'battle_format', battle_format\n  );\nEND;\n$$;\n\n-- Step 2: process_expired_battles関数を更新（引き分け時レーティング計算対応）\nCREATE OR REPLACE FUNCTION public.process_expired_battles()\nRETURNS void\nLANGUAGE plpgsql\nSECURITY DEFINER\nAS $$\nDECLARE\n  rec RECORD;\n  v_winner_id UUID;\n  v_loser_id UUID;\n  v_player1_rating INTEGER;\n  v_player2_rating INTEGER;\n  v_rating_calc JSON;\n  v_archived_battle_id UUID;\n  v_is_tie BOOLEAN;\nBEGIN\n  FOR rec IN\n    SELECT * FROM public.active_battles\n    WHERE end_voting_at < now() AND status = 'ACTIVE'\n  LOOP\n    BEGIN\n      -- 2a. Mark as processing to avoid double handling\n      UPDATE public.active_battles\n      SET status = 'PROCESSING_RESULTS', updated_at = now()\n      WHERE id = rec.id;\n\n      -- 2b. Determine winner, loser, and tie status\n      IF rec.votes_a > rec.votes_b THEN\n        v_winner_id := rec.player1_user_id;\n        v_loser_id := rec.player2_user_id;\n        v_is_tie := FALSE;\n      ELSIF rec.votes_b > rec.votes_a THEN\n        v_winner_id := rec.player2_user_id;\n        v_loser_id := rec.player1_user_id;\n        v_is_tie := FALSE;\n      ELSE\n        v_winner_id := NULL; -- tie\n        v_loser_id := NULL;\n        v_is_tie := TRUE;\n      END IF;\n\n      -- 2c. Update ratings (both for wins/losses AND ties)\n      -- Get current ratings for both players\n      SELECT rating INTO v_player1_rating \n      FROM public.profiles WHERE id = rec.player1_user_id;\n      \n      SELECT rating INTO v_player2_rating \n      FROM public.profiles WHERE id = rec.player2_user_id;\n\n      IF v_is_tie THEN\n        -- 🆕 Handle tie rating calculation using standard Elo (0.5 result)\n        SELECT calculate_tie_rating_with_format(v_player1_rating, v_player2_rating, rec.battle_format) INTO v_rating_calc;\n        \n        -- Update both players' ratings\n        UPDATE public.profiles \n        SET rating = (v_rating_calc->>'player1_rating')::INTEGER,\n            updated_at = now()\n        WHERE id = rec.player1_user_id;\n        \n        UPDATE public.profiles \n        SET rating = (v_rating_calc->>'player2_rating')::INTEGER,\n            updated_at = now()\n        WHERE id = rec.player2_user_id;\n        \n        -- Log tie rating changes\n        RAISE NOTICE 'TIE rating updated for battle %: Player1 % (% -> %), Player2 % (% -> %)', \n          rec.id, \n          rec.player1_user_id, v_player1_rating, (v_rating_calc->>'player1_rating')::INTEGER,\n          rec.player2_user_id, v_player2_rating, (v_rating_calc->>'player2_rating')::INTEGER;\n          \n      ELSE\n        -- Handle win/loss rating calculation (existing logic)\n        SELECT calculate_elo_rating_with_format(\n          CASE WHEN v_winner_id = rec.player1_user_id THEN v_player1_rating ELSE v_player2_rating END,\n          CASE WHEN v_winner_id = rec.player1_user_id THEN v_player2_rating ELSE v_player1_rating END,\n          rec.battle_format\n        ) INTO v_rating_calc;\n        \n        -- Update ratings based on winner/loser\n        IF v_winner_id = rec.player1_user_id THEN\n          -- Player 1 won\n          UPDATE public.profiles \n          SET rating = (v_rating_calc->>'winner_rating')::INTEGER,\n              updated_at = now()\n          WHERE id = rec.player1_user_id;\n          \n          UPDATE public.profiles \n          SET rating = (v_rating_calc->>'loser_rating')::INTEGER,\n              updated_at = now()\n          WHERE id = rec.player2_user_id;\n        ELSE\n          -- Player 2 won\n          UPDATE public.profiles \n          SET rating = (v_rating_calc->>'winner_rating')::INTEGER,\n              updated_at = now()\n          WHERE id = rec.player2_user_id;\n          \n          UPDATE public.profiles \n          SET rating = (v_rating_calc->>'loser_rating')::INTEGER,\n              updated_at = now()\n          WHERE id = rec.player1_user_id;\n        END IF;\n        \n        -- Log win/loss rating changes\n        RAISE NOTICE 'WIN/LOSS rating updated for battle %: Winner % (% -> %), Loser % (% -> %)', \n          rec.id, \n          v_winner_id, \n          CASE WHEN v_winner_id = rec.player1_user_id THEN v_player1_rating ELSE v_player2_rating END,\n          (v_rating_calc->>'winner_rating')::INTEGER,\n          v_loser_id,\n          CASE WHEN v_loser_id = rec.player1_user_id THEN v_player1_rating ELSE v_player2_rating END,\n          (v_rating_calc->>'loser_rating')::INTEGER;\n      END IF;\n\n      -- 2d. Archive into archived_battles\n      INSERT INTO public.archived_battles (\n        original_battle_id,\n        winner_id,\n        final_votes_a,\n        final_votes_b,\n        battle_format,\n        player1_user_id,\n        player2_user_id,\n        player1_submission_id,\n        player2_submission_id,\n        archived_at,\n        created_at,\n        updated_at\n      ) VALUES (\n        rec.id,\n        v_winner_id,\n        rec.votes_a,\n        rec.votes_b,\n        rec.battle_format,\n        rec.player1_user_id,\n        rec.player2_user_id,\n        rec.player1_submission_id,\n        rec.player2_submission_id,\n        now(),\n        now(),\n        now()\n      ) RETURNING id INTO v_archived_battle_id;\n\n      -- 2d-2. Copy vote comments to archived_battle_votes\n      INSERT INTO public.archived_battle_votes (\n        archived_battle_id,\n        user_id,\n        vote,\n        comment,\n        created_at\n      )\n      SELECT \n        v_archived_battle_id,\n        bv.user_id,\n        bv.vote,\n        bv.comment,\n        bv.created_at\n      FROM public.battle_votes bv\n      WHERE bv.battle_id = rec.id\n        AND bv.comment IS NOT NULL\n        AND bv.comment != '';\n\n      -- Log comment copying\n      RAISE NOTICE 'Copied % vote comments for archived battle %', \n        (SELECT COUNT(*) FROM public.battle_votes \n         WHERE battle_id = rec.id \n           AND comment IS NOT NULL \n           AND comment != ''), \n        v_archived_battle_id;\n\n      -- 2e. Update submissions status to BATTLE_ENDED\n      UPDATE public.submissions\n      SET status = 'BATTLE_ENDED', updated_at = now()\n      WHERE id IN (rec.player1_submission_id, rec.player2_submission_id);\n\n      -- 2f. Remove from active_battles (this will CASCADE delete battle_votes)\n      DELETE FROM public.active_battles WHERE id = rec.id;\n\n    EXCEPTION WHEN OTHERS THEN\n      -- 2g. Log error and continue with next battle\n      RAISE NOTICE 'Error processing battle %: %', rec.id, SQLERRM;\n    END;\n  END LOOP;\nEND;\n$$;\n\n-- Step 3: 権限設定\nGRANT EXECUTE ON FUNCTION calculate_tie_rating_with_format(INTEGER, INTEGER, TEXT) TO authenticated;\nGRANT EXECUTE ON FUNCTION public.process_expired_battles() TO postgres;\n\n-- Step 4: コメント追加\nCOMMENT ON FUNCTION calculate_tie_rating_with_format(INTEGER, INTEGER, TEXT) IS '引き分け時のレーティング計算関数：標準Elo方式（結果=0.5）でバトル形式別Kファクターを適用';\nCOMMENT ON FUNCTION public.process_expired_battles() IS '期限切れバトル処理関数：勝敗判定、レーティング更新（引き分け対応）、アーカイブ保存、投票コメント保持を実行';"}	implement_tie_rating_system	rikujiogino@gmail.com	\N
